CONNECT APPDB/gridnode;

---
--- data for table '"entitymetainfo"'
---
--- Field 1 ObjectName: full qualified class name of the entity
--- Field 2 EntityName: short class name of the entity
--- Field 3 SqlName: table name of the entity
DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('GWFRtProcess','GWFRtActivity','GWFRtRestriction','GWFTransActivationState','GWFRtProcessDoc');

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('UserAccount','UserAccountState');

DELETE FROM "entitymetainfo" WHERE "EntityName" LIKE '%WebService';
DELETE FROM "entitymetainfo" WHERE "EntityName" LIKE '%ServiceAssignment';

DELETE from "entitymetainfo" WHERE "EntityName" IN ('SearchQuery', 'Condition');

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('ProcessDef','ProcessAct');

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('PartnerType','PartnerGroup','Partner');

DELETE from "entitymetainfo" WHERE "EntityName" IN ('MappingFile','MappingRule','XpathMapping');

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('SchemaMapping');

DELETE FROM "entitymetainfo" WHERE "EntityName" = 'License';

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('GFDefinition','GFTemplate');

DELETE FROM "entitymetainfo" WHERE "EntityName" = 'CompanyProfile';

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('ChannelInfo','CommInfo','SecurityInfo','PackagingInfo','AS2PackagingInfoExtension','FlowControlInfo');

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('BusinessEntity','WhitePage','RegistryObjectMapping','SearchRegistryCriteria','SearchRegistryQuery','RegistryConnectInfo','DomainIdentifier');

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('Alert','AlertType','Action','MessageTemplate','AlertAction','AlertCategory','AlertList','EmailConfig','JmsDestination','MessageProperty','JmsMessageRecord');

DELETE FROM "entitymetainfo" WHERE "EntityName"='GWFArchiveProcess';

COMMIT;

