INSERT INTO message_template (UID, Name, ContentType, MessageType, FromAddr, ToAddr, CcAddr, Subject, Message, Location, Append, Version, CanDelete) VALUES (-6,'PROCESS_INSTANCE_FAILURE_EMAIL','Text','EMail','<#USER=admin#>','<#USER=admin#>','','Process Instance (<%Process.PROCESS_DEF_NAME%>/<%Process.PROCESS_ORIGINATOR_ID%>/<%Process.PROCESS_INSTANCE_ID%>) has failed for document <%GridDocument.U_DOC_FILENAME%>','Process Instance has failed for document <%GridDocument.U_DOC_FILENAME%> on <%System.CURRENT_DATETIME%>.\r\n\r\nProcess Details:\r\n----------------\r\nDefinition Name : <%Process.PROCESS_DEF_NAME%>\r\nOriginator ID   : <%Process.PROCESS_ORIGINATOR_ID%>\r\nInstance ID     : <%Process.PROCESS_INSTANCE_ID%>\r\n\r\nDocument Details:\r\n-----------------\r\nDocument Type : <%GridDocument.U_DOC_DOC_TYPE%>\r\nGridDoc ID    : <%GridDocument.G_DOC_ID%>\r\nFolder        : <%GridDocument.FOLDER%>\r\n\r\nError Details:\r\n--------------\r\nType     : <%ProcessingError.TYPE%>\r\n           [1] Maximum retries has reached but no acknowledge received\r\n           [2] Time to perform has expired but no response received\r\n           [3] Document validation has failed (See Reason and Exception)\r\n           [4] Failure Notification PIP has failed due to [1]\r\n           [5] RosettaNet Exception Message is received in Inbound folder\r\n           [6] Failure Notification PIP is received from Partner\r\n           [7] Failure Notification PIP is initiated (See Reason)\r\n           [8] User has cancelled the process\r\n\r\nReason   : <%ProcessingError.REASON%>\r\n\r\nException: <%ProcessingError.MESSAGE%>\r\nTrace:\r\n------\r\n<%ProcessingError.STACK_TRACE%>\r\n','',0,1,0);
