-- -----------------------------------------------------------------
-- This script Creates default data for some of the tables in APPDB
-- -----------------------------------------------------------------
SET client_encoding = 'UTF8';
SET check_function_bodies = false;
SET client_min_messages = warning;
SET search_path = appdb;

DELETE FROM "entitymetainfo" WHERE "EntityName" IN ('BusinessEntity','WhitePage','RegistryObjectMapping','SearchRegistryCriteria','SearchRegistryQuery','RegistryConnectInfo','DomainIdentifier');

--------- BusinessEntity
DELETE FROM "fieldmetainfo" WHERE "EntityObjectName" LIKE '%.BusinessEntity';

--------- WhitePage
DELETE FROM "fieldmetainfo" WHERE "EntityObjectName" LIKE '%.WhitePage';


--------- RegistryObjectMapping
DELETE FROM "fieldmetainfo" WHERE "EntityObjectName" LIKE '%.RegistryObjectMapping';

--------- SearchRegistryCriteria
DELETE FROM "fieldmetainfo" WHERE "EntityObjectName" LIKE '%.SearchRegistryCriteria';

--------- SearchRegistryQuery
DELETE FROM "fieldmetainfo" WHERE "EntityObjectName" LIKE '%.SearchRegistryQuery';

--------- RegistryConnectInfo
DELETE FROM "fieldmetainfo" WHERE "EntityObjectName" LIKE '%.RegistryConnectInfo';

--------- DomainIdentifier
DELETE FROM "fieldmetainfo" WHERE "EntityObjectName" LIKE '%.DomainIdentifier';
